import React from "react";
import bg from '../../assets/bg_.jpg'
import { useTranslation } from 'react-i18next'


const LineFirst = () => {
    const { t } = useTranslation();
    return(
        <div style={{
            backgroundImage: `url(${bg})`,
            backgroundSize: '100% 100%',
            backgroundRepeat: 'no-repeat'
            }} className='line_list'>
            <div className='container'>
                <div style={{maxWidth: 1000}}>
                <p className='line_list_title_last text-white'><b>{t('bitquant')}</b> {t('linFirstD')} <b>{t('lineNum')}</b> {t('lineFirstNext')}</p>
                <p style={{maxWidth: 630}} className='line_list_desc_last text-white mb-0'>{t('lineFirst')}</p>
                <a className='text-white' href="https://bitquant.online/"><button style={{width:282}} className='text-white'>{t('lineRirstButt')}</button></a>
                </div>
            </div>
        </div>
    )
}

export default LineFirst
