import React,{useState} from 'react'
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import {depositArr} from '../../storage/storage.js'
import 'swiper/css/navigation';
import { Navigation } from 'swiper';

const Pricing = () => {
    const [currentDeposit,setCurrentDeposit] = useState(0)
    return(
        <div className='pricing_container'>
            <div className='container'>
                <h2 className='text-white text-center'>Pricing</h2>
                <p className='text-white text-center'>Deposit amount</p>
                <div className='deposit_panel'>
                    {depositArr.map((el,key) => 
                       <div key={key}
                       onClick={() => setCurrentDeposit(key)}
                       className={key === currentDeposit? "active_deposit" :""}>
                        {el.num}$</div>
                    )}
                </div>
                <h1>IM HERE</h1>
                        <Swiper
                        modules={[Navigation]}
                        spaceBetween={24}
                        slidesPerView={1}
                        navigation>
                            {depositArr[currentDeposit].content.map((el,key) =>
                            <SwiperSlide> 
                                <div key={key} className='col-md-3 '>
                                    <div className='list_deposit_item m-1'>
                                        <h3>{el.name}</h3>
                                        <p className='period'>Period</p>
                                        <p className='period_time'>{el.period} month</p>
                                        <p className='price'>Price</p>
                                        <p className='price_item'>${el.price}</p>
                                    </div>
                                    <button>Start free trial</button>
                                    </div>
                                    </SwiperSlide>)}
                        </Swiper>
                <div className='row container_list_deposit'>
                        {depositArr[currentDeposit].content.map((el,key) => 
                        <div key={key} className='col-md-3 '>
                        <div className='list_deposit_item m-1'>
                            <h3>{el.name}</h3>
                            <p className='period'>Period</p>
                            <p className='period_time'>{el.period} month</p>
                            <p className='price'>Price</p>
                            <p className='price_item'>${el.price}</p>
                        </div>
                        <button>Start free trial</button>
                        </div>)}
                </div>
            </div>
        </div>
    )
}

export default Pricing
